import React, { useState, useEffect } from "react";
import axios from "axios";

const Timeline = () => {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    // Fetch the tweets from the backend API
    axios.get("http://localhost:5001/tweets")
      .then(response => {
        setTweets(response.data);
      })
      .catch(error => {
        console.error("Error fetching tweets:", error);
      });
  }, []);

  return (
    <div className="timeline">
      {tweets.map(tweet => (
        <div className="tweet" key={tweet.id}>
          <div className="user-info">
            <div className="avatar">
              <img src={`https://picsum.photos/50?random=${tweet.user_id}`} alt="Avatar" />
            </div>
            <div className="username">{tweet.username}</div>
            <div className="name">{tweet.name}</div>
          </div>
          <div className="content">
            <div className="tweet-text">{tweet.tweet}</div>
            <div className="actions">
              <button className="like-button">Like</button>
              <button className="retweet-button">Retweet</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Timeline;
