import React, { useState, useEffect } from "react";
import axios from "axios";
import Tweet from "./Tweet";
import "../styles/Timeline.css";

const Timeline = () => {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    // Fetch the tweets from the backend API
    axios.get("http://localhost:5001/tweets")
      .then(response => {
        setTweets(response.data);
      })
      .catch(error => {
        console.error("Error fetching tweets:", error);
      });
  }, []);

  return (
    <div className="Timeline">
      {tweets.map((tweet) => (
        <Tweet key={tweet.id} name={tweet.name} username={tweet.username} timestamp={tweet.timestamp} text={tweet.text} retweets={tweet.retweets} likes={tweet.likes} avatar={tweet.profile_image_url} />
      ))}
    </div>
  );
};

export default Timeline;
