import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Timeline.css";
import posthog from "posthog-js";

const Timeline = () => {
  const [tweets, setTweets] = useState([]);
  const [userInteractions, setUserInteractions] = useState({});

  useEffect(() => {
    // Fetch the tweets from the backend API
    axios.get("http://localhost:5001/tweets")
      .then(response => {
        setTweets(response.data);
      })
      .catch(error => {
        console.error("Error fetching tweets:", error);
      });
  }, []);
  function handleButtonClick() {
    posthog.capture("button_click", { button_text: "Example Button" });
    // Your button click logic here
  }
  const handleLikeClick = (tweetId) => {
    posthog.capture('like_click', { tweet_id: tweetId });
    toggleLike(tweetId);
  };
  const handleRetweetClick = (tweetId) => {
    posthog.capture('retweet_click', { tweet_id: tweetId });
    toggleRetweet(tweetId);
  };
  const toggleLike = (tweetId) => {
    const hasLiked = userInteractions[tweetId]?.hasLiked || false;

    setUserInteractions({
      ...userInteractions,
      [tweetId]: { ...userInteractions[tweetId], hasLiked: !hasLiked }
    });

    setTweets(tweets.map(tweet => tweet.id === tweetId ? { ...tweet, likes: hasLiked ? tweet.likes - 1 : tweet.likes + 1 } : tweet));
  };

  const toggleRetweet = (tweetId) => {
    const hasRetweeted = userInteractions[tweetId]?.hasRetweeted || false;

    setUserInteractions({
      ...userInteractions,
      [tweetId]: { ...userInteractions[tweetId], hasRetweeted: !hasRetweeted }
    });

    setTweets(tweets.map(tweet => tweet.id === tweetId ? { ...tweet, retweets: hasRetweeted ? tweet.retweets - 1 : tweet.retweets + 1 } : tweet));
  };
  return (
    <div className="Timeline">
      {tweets.map((tweet) => (
        <div key={tweet.id} className="Tweet">
        <img className="Tweet-avatar" src={tweet.profile_image_url} alt="Avatar" />
          <div className="Tweet-content">
            <div className="Tweet-header">
              <span className="Tweet-name">{tweet.name}</span>
              <span className="Tweet-username">@{tweet.username}</span>
              <span className="Tweet-timestamp">{tweet.timestamp}</span>
            </div>
            <div className="Tweet-text">{tweet.tweet}</div>
            <div className="Tweet-stats">
              <div className="Tweet-stat" onClick={() => handleRetweetClick(tweet.id)}>
                <svg
                  viewBox="0 0 24 24"
                  width="16"
                  height="16"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path d="M22 2v20l-4-4H2V6h16l4-4z"></path>
                </svg>
                {tweet.retweets_count}
              </div>
              <div className="Tweet-stat" onClick={() => handleLikeClick(tweet.id)}>
                <svg
                  viewBox="0 0 24 24"
                  width="16"
                  height="16"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                  <polyline points="22 4 12 14.01 9 11.01"></polyline>
                </svg>
                {tweet.likes_count}
              </div>
            </div>
          </div>
        </div>
      ))}
       <button onClick={handleButtonClick}>Example Button</button>
    </div>
  );
};

export default Timeline;
