import React, { useState, useEffect } from "react";
import axios from "axios";

const Tweet = ({ tweet, onLike, onRetweet }) => {
  return (
    <div className="tweet">
      <div className="tweet-content">{tweet.content}</div>
      <div className="tweet-actions">
        <button onClick={() => onLike(tweet.id)}>Like ({tweet.likes})</button>
        <button onClick={() => onRetweet(tweet.id)}>Retweet ({tweet.retweets})</button>
      </div>
    </div>
  );
};

const Timeline = () => {
  const [tweets, setTweets] = useState([]);

  useEffect(() => {
    async function fetchTweets() {
      try {
        const response = await axios.get("http://localhost:5000/tweets");
        setTweets(response.data);
      } catch (error) {
        console.error("Error fetching tweets:", error);
      }
    }

    fetchTweets();
  }, []);

  const handleLike = (tweetId) => {
    // Call the backend API to like the tweet here
  };

  const handleRetweet = (tweetId) => {
    // Call the backend API to retweet the tweet here
  };

  return (
    <div className="timeline">
      <h1>Timeline</h1>
      {tweets.map((tweet) => (
        <Tweet
          key={tweet.id}
          tweet={tweet}
          onLike={handleLike}
          onRetweet={handleRetweet}
        />
      ))}
    </div>
  );
};

export default Timeline;
